
public class Problem1 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 4, b = 8;
		
		System.out.println("4 + 8 = " + (a + b));
		System.out.println("4 - 8 = " + (a - b));
		System.out.println("4 * 8 = " + (a * b));
		System.out.println("8 / 4 = " + (b / a));
	}
}
